    public class QuarryWorldFile : Subworld
    {
        public override int width => 2400;
        public override int height => 2400;
        public override ModWorld modWorld => ModContent.GetInstance<WorldFile>();
        public override bool saveSubworld => false;
        public override bool disablePlayerSaving => true;
        public override bool saveModData => true;
        public static bool FirstTime = true;
        public override List<GenPass> tasks => new List<GenPass>()
        {
            new SubworldGenPass(progress =>
            {
                if (FirstTime)
                {
                    progress.Message = "Welcome to the Quarry!";
                    Main.worldSurface = 0;
                    Main.rockLayer = 0;
                    for (int i = 0; i < Main.maxTilesX; i++)
                    {
                        for (int j = 0; j < Main.maxTilesY; j++)
                        {
                            progress.Set((j + i * Main.maxTilesY) / (float)(Main.maxTilesX * Main.maxTilesY));
			                Main.tile[i, j].active(true);
                            Main.tile[i, j].type = (ushort)(ModContent.TileType<QuarrystoneAlt>());
                            bool success = false;
                            int attempts = 0;
                            while (!success)
                            {
                                attempts++;
                                if (attempts > 1000)
                                {
                                    success = true;
                                    continue;
                                }
                                success = FirstTimer();
                            }
                        }
                    }
                    FirstTime = false;
                }
                else
                {
                    progress.Message = "Cave Collapsed! Generating new Mineshaft";
                    Main.worldSurface = 0;
                    Main.rockLayer = 0;
                    for (int i = 0; i < Main.maxTilesX; i++)
                    {
                        for (int j = 0; j < Main.maxTilesY; j++)
                        {
                            progress.Set((j + i * Main.maxTilesY) / (float)(Main.maxTilesX * Main.maxTilesY));
			                Main.tile[i, j].active(true);
                            Main.tile[i, j].type = (ushort)(ModContent.TileType<Quarrystone>());
                        }
                    }
                }
            })
        };

