        public override void Load()
        {
            Main.dayTime = true;
            Main.time = 27000;
        }

        private bool FirstTimer()
        {
            for (int y = (Main.maxTilesY / 2) + 20; y < FirstTimeShape.GetLength(0); y++)
            {
                for (int x = (Main.maxTilesX / 2) + 17; x < FirstTimeShape.GetLength(1); x++)
                {
                    if (WorldGen.InWorld(y, x))
                    {
                        Tile tile = Framing.GetTileSafely(y, x);
                        switch (FirstTimeShape[y, x])
                        {
                            case 1:
                                tile.type = (ushort)(ModContent.TileType<QuarrystoneAlt>());
                                tile.active(true);
                                break;
                            case 2:
                                tile.type = (ushort)(ModContent.TileType<Quarrystone>());
                                tile.active(true);
                                break;
                        }
                        switch (FirstTimeWallShape[y, x])
                        {
                            case 1:
                                tile.wall = WallID.Stone;
                                break;
                            case 2:
                                tile.wall = WallID.WoodenFence;
                                break;
                        }
                        switch (FirstTimeLiquidShape[y, x])
                        {
                            case 1:
                                tile.liquid = 255;
                                tile.honey(true);
                                break;
                        }
                    }
                }
            }
            return true;
        }
    }
